import { Link, useLocation } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useEffect, useState } from 'react';


const Nav = () => {
    const user = useSelector(state => state.who);
    const location = useLocation();

    const [ styles, setStyles ] = useState(["",""])

    console.log(location)
    const handleDynamicNav = () => {
        if(location.pathname === "/"){
        setStyles(['black','white'])
        let test = document.getElementById('/home')
        test.style.background = styles[0];
        test.style.color = styles[1];
        }

    }
    useEffect(() => {
        handleDynamicNav()
    },[location])
    return(

        <div className="nav">
            <div className="nav-container">

                <Link id="/" to="/">LEIF</Link>
                <Link to="/school"> SCHOOL </Link>
                <Link to="/jobs"> WORK</Link>
                <Link to="/passions"> PASSION</Link>
                <Link to="/redux"> PLAY ZONE</Link>
                {user!== 'guest' ? <Link to="/name">Hi {user}</Link> : null }
            </div>

        </div>
    )
}

export default Nav;
