import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import { increment, decrement, loggin } from '../actions';


const Redux = () => {

    const [ username, setUsername ] = useState(null)
    const counter = useSelector(state => state.counter);
    const logged = useSelector(state => state.logged);
    const clicks = useSelector(state => state.clicks);

    const action = useDispatch();

    const handleForm = (e) => {
        setUsername(e.target.value)
        username = ""


    }

    return(
        <div className="reduxCountainer">
            <h1>Redux</h1>
            <p>Counter: {counter}</p>
            <button onClick={() => action(increment())}>+</button>
            <button onClick={() => action(decrement())}>-</button>
            <p>Are you Logged in? {logged}</p>
            <label>Your name?</label>
            <form onSubmit={ (e) => {
                e.preventDefault()
                console.log(username)
                action(loggin(username))
                setUsername("")
            }}>
                <input onChange={event => handleForm(event)} type="text" name="user" id="user"/>
                {username!== "" ? <input type="submit" value="SUBMIT"/> : <p>Is your name Juste Leblanc?</p>}
            </form>
            <p>You've clicked {clicks} times on this website</p>

        </div>
    )
};

export default Redux;
