import { Link, useLocation } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useEffect, useState } from 'react';


const Nav = () => {
    const user = useSelector(state => state.who);
    const location = useLocation();
    const pages = ['/','/school','/jobs','/passions','/redux'];

    const [ styles, setStyles ] = useState({"background":null, "color": null})


    const handleDynamicNav = () => {
        console.log(location.pathname)
        pages.map(page => page === location.pathname ? setState({"background":"black"}) : console.log('no'))

    }
    useEffect(() => {
        handleDynamicNav()
    },[location])
    return(

        <div className="nav">
            <div className="nav-container">

                <Link style={styles.background, styles.color} to="/">LEIF</Link>
                <Link to="/school"> SCHOOL </Link>
                <Link to="/jobs"> WORK</Link>
                <Link to="/passions"> PASSION</Link>
                <Link to="/redux"> PLAY ZONE</Link>
                {user!== 'guest' ? <Link to="/name">Hi {user}</Link> : null }
            </div>

        </div>
    )
}

export default Nav;
