import { Link, useLocation } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useEffect, useState } from 'react';


const Nav = () => {
    const user = useSelector(state => state.who);
    const location = useLocation();

    const [ styles, setStyles ] = useState(["test",{"background":null, "color": null}])

    console.log(styles)
    const handleDynamicNav = () => {
        let test = document.getElementById('home')
        console.log(test)
        test.background = "red"

    }
    useEffect(() => {
        handleDynamicNav()
    },[location])
    return(

        <div className="nav">
            <div className="nav-container">

                <Link id="home" to="/">LEIF</Link>
                <Link to="/school"> SCHOOL </Link>
                <Link to="/jobs"> WORK</Link>
                <Link to="/passions"> PASSION</Link>
                <Link to="/redux"> PLAY ZONE</Link>
                {user!== 'guest' ? <Link to="/name">Hi {user}</Link> : null }
            </div>

        </div>
    )
}

export default Nav;
